function i(){document.querySelectorAll('[x-ordo-utils="toggleElementsOnClick"]').forEach(n=>{n.addEventListener("click",l=>{l.preventDefault();const t=n.getAttribute("data-element-to-show"),o=n.getAttribute("data-element-to-hide");if(o){const e=$(o);e?e.fadeOut():console.error(`Element ID "${o}" not found on the page`)}if(t){const e=$(t);e?e.fadeIn():console.error(`Element ID "${t}" not found on the page`)}!t&&!o&&console.error("Missing data-element-to-show or data-element-to-hide id attribute")})})}i();
